<html><head>    <style>        body {            font-family: arial, Helvetica, sans-serif;        }        p {            font-size: 12px;        }        .header_bar {            width: 100%;            height: 40px;            text-align: center;        }        #front_page_title_container {            margin-top: 20px;            width: 100%;            text-align: center;        }        #prop_description_container {            width: 100%;            text-align: left;            padding-top: 20px;        }        #prop_description_container p {            font-size: 14px;        }        .prop_quick_facts_container p {            margin-top: 5px;            margin-bottom: 5px;        }        #pic_and_facts_container {            width: 100%;            padding-top: 20px;        }        .quick_facts {            padding-left: 30px;            font-size: 16px;        }        .quick_facts p {            font-size: 16px;        }        #prop_title_container h3 {            margin-bottom: 0;        }        .qr_cell {            text-align: right;        }        .links_table {            width: 100%;        }        img.displayed {            display: block;            margin-left: auto;            margin-right: auto }    </style></head><body><div class="header_bar">    <div class="header_bar_logo">        <img src="<?=$_SERVER['DOCUMENT_ROOT']?>/assets/img/logo_dark.png" height="90"/>    </div></div><div id="front_page_title_container">    <h2><?= strtoupper($property_data['generic_street_name']) ?></h2>        <?= strtoupper($property_data['city']) ?>, <?= strtoupper($property_data['county']) ?>, <?= strtoupper($property_data['postcode']) ?>    </h3></div><div id="pic_and_facts_container"><table class="pic_and_facts_table">    <tr>        <td>            <!--<img class="main_img" src="http://www.rent-direct.co.uk/phpthumb/phpThumb.php?w=400&h=300&zc=C&src=/property_images/Oak-Cottage4194520140729115422.jpg" width="400"/>-->            <img class="main_img" src="<?=$_SERVER['DOCUMENT_ROOT']?>/prop_images/<?=$property_data['main_image']?>" width="400"/>        </td>        <td class="quick_facts">            <? if (isset($property_data['has_parking']) == 1) { $parking = 'Yes'; }               if (isset($property_data['has_parking']) == 0) { $parking = 'No'; }            ?>            <p>Rent: &pound;<? if (isset($property_data['rent'])) { echo (int)$property_data['rent']; } ?> P/M </p>            <p>Bond: &pound;<? if (isset($property_data['bond'])) { echo (int)$property_data['bond']; } ?> </p>            <p>Available From: <? if (isset($property_data['date_available'])) { echo date('d/m/Y',strtotime($property_data['date_available'])); } ?> </p>            <p>Bedrooms: <? if (isset($property_data['bedrooms'])) { echo $property_data['bedrooms']; } ?> </p>            <p>Bathrooms: <? if (isset($property_data['bathrooms'])) { echo $property_data['bathrooms']; } ?> </p>            <p>Furnished: <? if (isset($property_data['furnished'])) { echo $property_data['furnished']; } ?> </p>            <p>Pets: <? if (isset($property_data['pets_allowed'])) { echo $property_data['pets_allowed']; } ?> </p>            <p>Smoking: <? if (isset($property_data['smoking_allowed'])) { echo $property_data['smoking_allowed']; } ?> </p>            <p>DHSS: <? if (isset($property_data['dhss_allowed'])) { echo $property_data['dhss_allowed']; } ?> </p>            <p>Parking: <? echo $parking ?> </p>        </td>    </tr></table></div><div id="prop_title_container">    <h3><?=$property_data['title']?></h3></div><div id="prop_description_container">    <p><?= strip_tags($property_data['description']); ?></p></div><div id='qr_code_container'>    <h3>View this property online:</h3>    <table class="links_table">        <tr>            <td><a href="http://www.rent-direct.co.uk/properties/view/<?=$property_data['slug']?>">http://www.rent-direct.co.uk/properties/view/<?=$property_data['slug']?></a></td><br /><br /><td class="qr_cell"><img src="<?=$_SERVER['DOCUMENT_ROOT']?>/qr_codes/<?=$property_data['id']?>.png" /></td>        </tr>    </table></div></body></html>